$(document).ready(function(){var n=$(".datatable-recipients").DataTable({searching:!1,info:!1,responsive:{details:{display:$.fn.dataTable.Responsive.display.childRow}}});$(".template-radio:checked").length>0&&($(".instructionText").text("Select the recipient(s) you would like to schedule emails for"),$(".recipient-container").show());$(".template-radio").change(function(){$(".instructionText").text("Select the recipient(s) you would like to schedule emails for");$(".recipient-container").show()});$(".recipient-checkbox-selectAll").change(function(){$(".recipient-checkbox").prop("checked",this.checked);this.checked?$(".recipient-selection").prop("disabled",!1):$(".recipient-selection").prop("disabled",!0)});$(document).on("change",".recipient-checkbox",function(){this.checked?$(this).siblings(".recipient-selection").first().prop("disabled",!1):$(this).siblings(".recipient-selection").first().prop("disabled",!0)});$(".btn-recipient").click(function(){var t=$.trim($(".input-recipient").val()),i=$(".recipient-email").filter(function(){return $.trim($(this).text().toUpperCase())==$.trim(t.toUpperCase())});$(".input-recipient").val()&&i.length<=0&&n.row.add([`<input type="checkbox" class="recipient-checkbox" checked/><input class="recipient-selection" type="hidden" name="SelectedRecipientEmailAddresses" value="${t}"/>`,`<span class="recipient-email">${t}</span>`]).draw()})});